mapper.select:
select id, password, name, email, role, phone, regdate, enabled
from members
where id = #{id}

mapper.update:
update members 
set password = #{password}, email = #{email}
where id = #{id}

mapper.select:
SELECT COUNT(*) 
FROM members 
WHERE id = #{id}

mapper.update:
UPDATE members 
SET enabled = FALSE
WHERE id = #{id}
